create table answer (answerid integer  generated by default as identity, content varchar(255), value double not null, metri_metricid integer, primary key (answerid));
create table classification (classificationid integer generated by default as identity, description varchar(255), name varchar(255), primary key (classificationid));
create table framework (frameworkid integer generated by default as identity, descriptionfw varchar(255), namefw varchar(255), primary key (frameworkid));
create table framework_evaluation (framework_evaluationid integer generated by default as identity, classificationid integer, frameworkid integer, primary key (framework_evaluationid));
create table metric (metricid integer  generated by default as identity, description varchar(255), primary key (metricid));
create table question (questionid integer generated by default as identity, question varchar(255), metricid integer, require_requirementid integer, primary key (questionid));
create table requirement (requirementid integer generated by default as identity, content varchar(255), classi_classificationid integer, category_categoryid integer, priority integer, primary key (requirementid));
CREATE TABLE feva_result (feva_resultid integer  generated by default as identity, framework_evaluationid integer, questionid integer, answerid integer, primary key (feva_resultid));
CREATE TABLE category (categoryid integer generated by default as identity, category VARCHAR (255), primary key (categoryid));
CREATE TABLE user (userid integer generated by default as identity, username VARCHAR (255),password VARCHAR (255), role VARCHAR (255), primary key (userid));
CREATE TABLE role_access (role_accessid INTEGER generated by DEFAULT as IDENTITY, role VARCHAR (255), read_access BIT, create_classi BIT, create_req BIT, create_quest BIT, create_metric BIT, evaluation BIT, admin_access BIT, create_category BIT, primary key (role_accessid));